// Valida√ß√£o e prepara√ß√£o de entrada
const input = $input.first().json;

if (!input.message || !input.user_id) {
  throw new Error('Mensagem e user_id s√£o obrigat√≥rios');
}

// Sanitiza√ß√£o b√°sica
function sanitizeInput(str) {
  return str
    .trim()
    .substring(0, 2000)
    .replace(/<script[^>]*>.*?<\/script>/gi, '')
    .replace(/<[^>]+>/g, '');
}

const context = {
  user_id: input.user_id,
  session_id: input.session_id || `session_${Date.now()}`,
  message: sanitizeInput(input.message),
  original_message: input.message,
  timestamp: new Date().toISOString(),
  request_id: `req_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`,
  metadata: {
    source: input.source || 'web',
    language: input.language || 'pt-BR',
    timezone: input.timezone || 'America/Sao_Paulo',
    client_info: input.client_info || {},
    force_refresh: input.force_refresh || false
  }
};

return {json: context};

const message = $json.message.toLowerCase();
const context = $json;

// Configura√ß√£o de intents com patterns melhorados
const intents = {
  carga_energia: {
    keywords: ['carga', 'consumo', 'demanda', 'energia', 'mw', 'megawatt', 'load'],
    patterns: [
      /(?:qual|quanto).{0,20}(?:carga|consumo|demanda)/i,
      /carga.{0,20}(?:atual|agora|hoje)/i,
      /energia.{0,20}(?:consumida|utilizada)/i
    ],
    confidence: 0
  },
  cmo_pld: {
    keywords: ['cmo', 'pld', 'preco', 'pre√ßo', 'custo', 'marginal', 'operacao', 'opera√ß√£o'],
    patterns: [
      /(?:qual|quanto).{0,20}(?:cmo|pld|pre√ßo|preco)/i,
      /custo.{0,20}marginal/i,
      /pre√ßo.{0,20}energia/i
    ],
    confidence: 0
  },
  bandeiras: {
    keywords: ['bandeira', 'tarifa', 'tarifaria', 'tarif√°ria', 'verde', 'amarela', 'vermelha'],
    patterns: [
      /bandeira.{0,20}(?:tarifaria|tarif√°ria|atual)/i,
      /(?:verde|amarela|vermelha).{0,20}(?:bandeira|tarifa)/i
    ],
    confidence: 0
  },
  geracao: {
    keywords: ['geracao', 'gera√ß√£o', 'producao', 'produ√ß√£o', 'usina', 'solar', 'eolica', 'e√≥lica', 'hidreletrica', 'hidrel√©trica', 'termica', 't√©rmica'],
    patterns: [
      /(?:geracao|gera√ß√£o|producao|produ√ß√£o).{0,20}(?:energia|eletrica|el√©trica)/i,
      /(?:solar|eolica|e√≥lica|hidro|termica|t√©rmica)/i
    ],
    confidence: 0
  },
  comparacao: {
    keywords: ['comparar', 'versus', 'diferenca', 'diferen√ßa', 'melhor', 'pior', 'ranking', 'maior', 'menor'],
    patterns: [
      /compar(?:ar|a√ß√£o|e)/i,
      /(?:melhor|pior|maior|menor).{0,20}(?:regi√£o|subsistema|per√≠odo)/i,
      /diferen√ß?a.{0,20}entre/i
    ],
    confidence: 0
  },
  previsao: {
    keywords: ['previsao', 'previs√£o', 'projecao', 'proje√ß√£o', 'futuro', 'tendencia', 'tend√™ncia', 'proximo', 'pr√≥ximo', 'estimativa'],
    patterns: [
      /(?:previsao|previs√£o|projecao|proje√ß√£o)/i,
      /(?:proximo|pr√≥ximo|futuro).{0,20}(?:mes|m√™s|semana|dia|ano)/i,
      /tend√™nc?ia/i
    ],
    confidence: 0
  }
};

// Calcular confidence scores
for (const [intent, data] of Object.entries(intents)) {
  // Check keywords
  data.keywords.forEach(keyword => {
    if (message.includes(keyword)) {
      data.confidence += 0.3;
    }
  });

  // Check patterns (mais peso)
  if (data.patterns) {
    data.patterns.forEach(pattern => {
      if (pattern.test(message)) {
        data.confidence += 0.5;
      }
    });
  }

  // Normalizar confidence (m√°ximo 1.0)
  data.confidence = Math.min(1.0, data.confidence);
}

// Identificar intent prim√°rio
const sortedIntents = Object.entries(intents)
  .sort((a, b) => b[1].confidence - a[1].confidence);

const primaryIntent = sortedIntents[0];
const secondaryIntents = sortedIntents.slice(1, 3)
  .filter(([_, data]) => data.confidence > 0.3);

// Extra√ß√£o de entidades melhorada
const entities = {
  regions: [],
  periods: [],
  metrics: [],
  values: [],
  comparisons: []
};

// Regi√µes/Subsistemas
const regionMap = {
  'sudeste': ['sudeste', 'se', 'sp', 'rj', 'mg', 'es'],
  'sul': ['sul', 's', 'pr', 'sc', 'rs'],
  'nordeste': ['nordeste', 'ne', 'ba', 'pe', 'ce', 'rn', 'pb', 'al', 'se', 'ma', 'pi'],
  'norte': ['norte', 'n', 'am', 'pa', 'to', 'ro', 'ac', 'ap', 'rr'],
  'centro-oeste': ['centro-oeste', 'centro oeste', 'co', 'mt', 'ms', 'go', 'df']
};

for (const [region, keywords] of Object.entries(regionMap)) {
  if (keywords.some(kw => message.includes(kw))) {
    entities.regions.push(region);
  }
}

// Per√≠odos temporais
const periodPatterns = [
  { pattern: /hoje/gi, value: 'today', type: 'day' },
  { pattern: /ontem/gi, value: 'yesterday', type: 'day' },
  { pattern: /agora/gi, value: 'now', type: 'realtime' },
  { pattern: /(?:ultima|√∫ltima) semana/gi, value: 'last_week', type: 'week' },
  { pattern: /(?:ultimo|√∫ltimo) (?:mes|m√™s)/gi, value: 'last_month', type: 'month' },
  { pattern: /este (?:mes|m√™s)/gi, value: 'this_month', type: 'month' },
  { pattern: /este ano/gi, value: 'this_year', type: 'year' },
  { pattern: /\d{1,2}\/\d{1,2}\/\d{2,4}/gi, value: 'specific_date', type: 'date' },
  { pattern: /(janeiro|fevereiro|mar√ßo|abril|maio|junho|julho|agosto|setembro|outubro|novembro|dezembro)/gi, value: 'month_name', type: 'month' }
];

periodPatterns.forEach(({pattern, value, type}) => {
  const matches = message.match(pattern);
  if (matches) {
    entities.periods.push({
      value: value,
      type: type,
      text: matches[0]
    });
  }
});

// Valores num√©ricos
const valuePattern = /\d+(?:[.,]\d+)?\s*(?:mw|gwh|r\$|reais)?/gi;
const valueMatches = message.match(valuePattern);
if (valueMatches) {
  entities.values = valueMatches;
}

// An√°lise de necessidade de dados
const requiresData = primaryIntent[1].confidence > 0.3 && !message.match(/^(oi|ol√°|bom dia|boa tarde|boa noite|tchau|obrigado)/i);
const isGreeting = /^(oi|ol√°|bom dia|boa tarde|boa noite|hi|hello)/i.test(message);
const isHelp = /^(ajuda|help|como|tutorial|o que voc√™|o que voce)/i.test(message);
const isFarewell = /(tchau|at√©|ate logo|obrigado|valeu)/i.test(message);

// Verificar se precisa dados mais recentes
const needsFreshData = context.metadata.force_refresh || 
  entities.periods.some(p => ['now', 'realtime', 'today'].includes(p.value));

return {json: {
  ...context,
  analysis: {
    primary_intent: primaryIntent[0],
    confidence: primaryIntent[1].confidence,
    secondary_intents: secondaryIntents.map(([name, data]) => ({
      name,
      confidence: data.confidence
    })),
    all_intents: intents,
    entities: entities,
    requires_data: requiresData,
    needs_fresh_data: needsFreshData,
    is_greeting: isGreeting,
    is_help: isHelp,
    is_farewell: isFarewell,
    complexity: entities.regions.length > 1 || entities.periods.length > 1 ? 'complex' : 'simple'
  }
}};

const analysis = $json.analysis;
const intent = analysis.primary_intent;
const entities = analysis.entities;
const template = $('get-query-template').first()?.json;

let query = '';
let params = [];

// Determinar per√≠odo
let startDate = new Date();
let endDate = new Date();

if (entities.periods.length > 0) {
  const period = entities.periods[0];
  switch(period.value) {
    case 'today':
      startDate.setHours(0, 0, 0, 0);
      break;
    case 'yesterday':
      startDate.setDate(startDate.getDate() - 1);
      endDate = new Date(startDate);
      break;
    case 'last_week':
      startDate.setDate(startDate.getDate() - 7);
      break;
    case 'last_month':
      startDate.setMonth(startDate.getMonth() - 1);
      break;
    case 'this_month':
      startDate.setDate(1);
      startDate.setHours(0, 0, 0, 0);
      break;
    case 'this_year':
      startDate = new Date(startDate.getFullYear(), 0, 1);
      break;
    default:
      startDate.setDate(startDate.getDate() - 7);
  }
} else {
  // Padr√£o: √∫ltimos 7 dias
  startDate.setDate(startDate.getDate() - 7);
}

// Usar template se dispon√≠vel
if (template && template.query_template) {
  query = template.query_template;
  // Substituir placeholders
  query = query.replace('{{start_date}}', startDate.toISOString());
  query = query.replace('{{end_date}}', endDate.toISOString());

  if (entities.regions.length > 0) {
    query = query.replace('{{regions}}', entities.regions.map(r => `'${r}'`).join(','));
  }
} else {
  // Queries padr√£o por intent
  switch(intent) {
    case 'carga_energia':
      query = `
        WITH hourly_data AS (
          SELECT 
            DATE_TRUNC('hour', period_start) as period,
            subsystem,
            AVG(value) as avg_load,
            MAX(value) as max_load,
            MIN(value) as min_load,
            STDDEV(value) as std_dev,
            COUNT(*) as data_points
          FROM data_records
          WHERE dataset_id IN ('carga_energia', 'load')
            AND period_start BETWEEN $1 AND $2
            ${entities.regions.length > 0 ? "AND LOWER(subsystem) = ANY($3)" : ""}
          GROUP BY period, subsystem
        ),
        stats AS (
          SELECT
            subsystem,
            AVG(avg_load) as total_avg,
            MAX(max_load) as peak_load,
            MIN(min_load) as min_load
          FROM hourly_data
          GROUP BY subsystem
        )
        SELECT * FROM hourly_data
        UNION ALL
        SELECT 
          NULL as period,
          subsystem || '_stats' as subsystem,
          total_avg as avg_load,
          peak_load as max_load,
          min_load,
          0 as std_dev,
          0 as data_points
        FROM stats
        ORDER BY period DESC NULLS LAST, subsystem`;

      params = [startDate, endDate];
      if (entities.regions.length > 0) {
        params.push(entities.regions);
      }
      break;

    case 'cmo_pld':
      query = `
        WITH daily_data AS (
          SELECT 
            DATE_TRUNC('day', period_start) as period,
            subsystem,
            AVG(value) as avg_price,
            MAX(value) as max_price,
            MIN(value) as min_price,
            JSONB_AGG(
              DISTINCT processed_data->>'patamar'
            ) as patamares
          FROM data_records
          WHERE dataset_id IN ('cmo_pld', 'cmo', 'pld')
            AND period_start BETWEEN $1 AND $2
            ${entities.regions.length > 0 ? "AND LOWER(subsystem) = ANY($3)" : ""}
          GROUP BY period, subsystem
        )
        SELECT 
          period,
          subsystem,
          avg_price,
          max_price,
          min_price,
          patamares,
          RANK() OVER (PARTITION BY period ORDER BY avg_price DESC) as price_rank
        FROM daily_data
        ORDER BY period DESC, subsystem`;

      params = [startDate, endDate];
      if (entities.regions.length > 0) {
        params.push(entities.regions);
      }
      break;

    case 'bandeiras':
      query = `
        SELECT 
          period_start as period,
          value as tariff_value,
          processed_data->>'bandeira' as flag_color,
          processed_data->>'vigencia' as validity,
          LAG(value) OVER (ORDER BY period_start) as previous_value,
          value - LAG(value) OVER (ORDER BY period_start) as change
        FROM data_records
        WHERE dataset_id = 'bandeiras_tarifarias'
          AND period_start >= $1
        ORDER BY period_start DESC
        LIMIT 12`;

      params = [startDate];
      break;

    default:
      // Query gen√©rica
      query = `
        SELECT 
          dataset_id,
          period_start,
          subsystem,
          metric_name,
          value,
          unit,
          processed_data
        FROM data_records
        WHERE period_start BETWEEN $1 AND $2
        ORDER BY period_start DESC
        LIMIT 100`;

      params = [startDate, endDate];
  }
}

// Verificar se dados est√£o desatualizados
const checkFreshness = analysis.needs_fresh_data;

return {json: {
  query: query,
  params: params,
  context: $json,
  dataset_id: template?.dataset_id || intent,
  check_freshness: checkFreshness
}};

const userMessage = $('validate-input').first().json.message;
const analysis = $('analyze-intent').first().json.analysis;
const sessionCache = $('get-session-cache').first()?.json || {};
const chatHistory = $('get-chat-history').all().map(item => item.json) || [];
const queryResults = $('execute-query').all().map(item => item.json) || [];
const dataAge = $('check-data-age').first()?.json || {};

// Construir hist√≥rico de conversa√ß√£o
const conversationHistory = chatHistory
  .filter(msg => msg.role && msg.content)
  .map(msg => ({
    role: msg.role === 'user' ? 'user' : 'assistant',
    content: msg.content
  }))
  .reverse()
  .slice(-10); // √öltimas 10 mensagens

// Processar resultados dos dados
let dataContext = '';
let statistics = {};

if (queryResults && queryResults.length > 0) {
  const data = queryResults.flat();

  if (analysis.primary_intent === 'carga_energia') {
    const statsData = data.filter(row => row.subsystem && row.subsystem.includes('_stats'));
    const timeData = data.filter(row => row.period);

    if (statsData.length > 0) {
      const avgLoad = statsData.reduce((sum, row) => sum + (row.avg_load || 0), 0) / statsData.length;
      const maxLoad = Math.max(...statsData.map(row => row.max_load || 0));

      statistics = {
        avg_load: avgLoad.toFixed(2),
        peak_load: maxLoad.toFixed(2),
        data_points: timeData.length,
        subsystems: [...new Set(statsData.map(row => row.subsystem.replace('_stats', '')))]
      };

      dataContext = `Dados de Carga de Energia:\n`;
      dataContext += `- Carga m√©dia: ${avgLoad.toFixed(2)} MWmed\n`;
      dataContext += `- Carga m√°xima: ${maxLoad.toFixed(2)} MWmed\n`;
      dataContext += `- Subsistemas analisados: ${statistics.subsystems.join(', ')}\n`;
      dataContext += `- Per√≠odo: ${timeData.length} registros`;
    }
  } else if (analysis.primary_intent === 'cmo_pld') {
    const avgPrice = data.reduce((sum, row) => sum + (row.avg_price || 0), 0) / data.length;
    const maxPrice = Math.max(...data.map(row => row.max_price || 0));
    const minPrice = Math.min(...data.map(row => row.min_price || 0));

    statistics = {
      avg_price: avgPrice.toFixed(2),
      max_price: maxPrice.toFixed(2),
      min_price: minPrice.toFixed(2),
      subsystems: [...new Set(data.map(row => row.subsystem))].filter(Boolean)
    };

    dataContext = `Dados de CMO/PLD:\n`;
    dataContext += `- CMO m√©dio: R$ ${avgPrice.toFixed(2)}/MWh\n`;
    dataContext += `- CMO m√°ximo: R$ ${maxPrice.toFixed(2)}/MWh\n`;
    dataContext += `- CMO m√≠nimo: R$ ${minPrice.toFixed(2)}/MWh`;
  } else if (analysis.primary_intent === 'bandeiras') {
    const latestFlag = data[0];
    if (latestFlag) {
      statistics = {
        current_flag: latestFlag.flag_color,
        current_value: latestFlag.tariff_value,
        change: latestFlag.change || 0
      };

      dataContext = `Bandeira Tarif√°ria Atual:\n`;
      dataContext += `- Cor: ${latestFlag.flag_color}\n`;
      dataContext += `- Valor: R$ ${latestFlag.tariff_value}/100kWh\n`;
      dataContext += `- Varia√ß√£o: ${latestFlag.change ? (latestFlag.change > 0 ? '+' : '') + latestFlag.change.toFixed(2) : 'N/A'}`;
    }
  }
}

// Informa√ß√£o sobre freshness dos dados
let freshnessContext = '';
if (dataAge.hours_old) {
  if (dataAge.hours_old > 24) {
    freshnessContext = `‚ö†Ô∏è Aten√ß√£o: Os dados mais recentes t√™m ${Math.round(dataAge.hours_old)} horas. Uma atualiza√ß√£o foi solicitada.`;
  } else {
    freshnessContext = `‚úì Dados atualizados h√° ${Math.round(dataAge.hours_old)} horas.`;
  }
}

// Construir prompt para IA
const systemPrompt = `Voc√™ √© o AIDE, um assistente especializado em dados do setor el√©trico brasileiro. 
Sempre responda em portugu√™s brasileiro de forma clara, precisa e educativa.
Use os dados fornecidos para fundamentar suas respostas.
Quando apropriado, sugira an√°lises adicionais ou perguntas relacionadas.`;

const contextPrompt = `Contexto da Conversa:
- Inten√ß√£o identificada: ${analysis.primary_intent} (confian√ßa: ${(analysis.confidence * 100).toFixed(1)}%)
- Entidades detectadas: ${JSON.stringify(analysis.entities)}
${dataContext ? `\nDados Dispon√≠veis:\n${dataContext}` : ''}
${freshnessContext ? `\n${freshnessContext}` : ''}`;

// Adicionar contexto da sess√£o se dispon√≠vel
let sessionContext = '';
if (sessionCache.preferences) {
  sessionContext = `\nPrefer√™ncias do usu√°rio: ${JSON.stringify(sessionCache.preferences)}`;
}

return {json: {
  model: 'gpt-4-turbo-preview',
  messages: [
    { role: 'system', content: systemPrompt },
    { role: 'system', content: contextPrompt + sessionContext },
    ...conversationHistory,
    { role: 'user', content: userMessage }
  ],
  temperature: 0.7,
  max_tokens: 1500,
  functions: [
    {
      name: 'generate_visualization',
      description: 'Gerar configura√ß√£o para visualiza√ß√£o de dados',
      parameters: {
        type: 'object',
        properties: {
          chart_type: {
            type: 'string',
            enum: ['line', 'bar', 'pie', 'area', 'scatter', 'heatmap']
          },
          title: { type: 'string' },
          data: { type: 'object' }
        },
        required: ['chart_type', 'title', 'data']
      }
    }
  ],
  metadata: {
    session_id: $('validate-input').first().json.session_id,
    user_id: $('validate-input').first().json.user_id,
    request_id: $('validate-input').first().json.request_id,
    intent: analysis.primary_intent,
    statistics: statistics,
    data_age: dataAge
  }
}};

const aiResponse = $json;
const metadata = $('prepare-ai-context').first().json.metadata;
const queryData = $('execute-query').all().map(item => item.json);
const startTime = new Date($('validate-input').first().json.timestamp);

// Extrair resposta e fun√ß√£o (se houver)
let responseContent = '';
let functionCall = null;

if (aiResponse.choices && aiResponse.choices[0]) {
  const choice = aiResponse.choices[0];
  responseContent = choice.message.content || '';

  if (choice.message.function_call) {
    try {
      functionCall = {
        name: choice.message.function_call.name,
        arguments: JSON.parse(choice.message.function_call.arguments)
      };
    } catch (e) {
      console.error('Error parsing function call:', e);
    }
  }
}

// Determinar necessidade de visualiza√ß√£o
const needsVisualization = functionCall?.name === 'generate_visualization' ||
  responseContent.toLowerCase().includes('gr√°fico') ||
  responseContent.toLowerCase().includes('visualiza√ß√£o') ||
  metadata.intent === 'comparacao' ||
  (queryData && queryData.length > 5);

// Preparar dados para visualiza√ß√£o
function prepareVisualizationData(intent, data) {
  if (!data || data.length === 0) return null;

  const flatData = data.flat();

  if (intent === 'carga_energia') {
    const timeData = flatData
      .filter(row => row.period && !row.subsystem?.includes('_stats'))
      .sort((a, b) => new Date(a.period) - new Date(b.period));

    if (timeData.length === 0) return null;

    const subsystems = [...new Set(timeData.map(row => row.subsystem))];

    return {
      type: 'line',
      title: 'Evolu√ß√£o da Carga de Energia',
      data: {
        labels: [...new Set(timeData.map(row => new Date(row.period).toISOString()))],
        datasets: subsystems.map(subsystem => ({
          label: subsystem,
          data: timeData
            .filter(row => row.subsystem === subsystem)
            .map(row => row.avg_load),
          borderColor: getColor(subsystem),
          fill: false
        }))
      },
      options: {
        responsive: true,
        scales: {
          y: {
            title: {
              display: true,
              text: 'Carga (MWmed)'
            }
          }
        }
      }
    };
  } else if (intent === 'cmo_pld') {
    const priceData = flatData
      .filter(row => row.period)
      .sort((a, b) => new Date(a.period) - new Date(b.period));

    return {
      type: 'bar',
      title: 'CMO/PLD por Subsistema',
      data: {
        labels: [...new Set(priceData.map(row => row.subsystem))],
        datasets: [{
          label: 'Pre√ßo M√©dio (R$/MWh)',
          data: priceData.map(row => row.avg_price),
          backgroundColor: 'rgba(54, 162, 235, 0.5)'
        }]
      }
    };
  } else if (intent === 'bandeiras') {
    const flagData = flatData.slice(0, 12);

    return {
      type: 'line',
      title: 'Hist√≥rico de Bandeiras Tarif√°rias',
      data: {
        labels: flagData.map(row => new Date(row.period).toLocaleDateString('pt-BR')),
        datasets: [{
          label: 'Valor (R$/100kWh)',
          data: flagData.map(row => row.tariff_value),
          borderColor: 'rgb(255, 99, 132)',
          stepped: true
        }]
      }
    };
  }

  return null;
}

// Fun√ß√£o auxiliar para cores
function getColor(subsystem) {
  const colors = {
    'Sudeste': '#1f77b4',
    'Sul': '#ff7f0e',
    'Nordeste': '#2ca02c',
    'Norte': '#d62728',
    'Centro-Oeste': '#9467bd'
  };
  return colors[subsystem] || '#666666';
}

// Gerar sugest√µes contextuais
function generateSuggestions(intent, entities) {
  const suggestions = {
    'carga_energia': [
      'Comparar com per√≠odo anterior',
      'Ver previs√£o para pr√≥ximas horas',
      'Analisar pico de consumo',
