const metric = $('process-metrics').first().json;
const result = $input.first().json;
const checkTime = new Date();

let status = 'ok';
let value = null;
let message = '';
let details = {};

// Verificar se houve erro
if (result.error) {
  status = 'error';
  message = result.error?.message || 'Check failed';
  details.error = result.error;
} else {
  // Processar resultado por tipo de métrica
  switch(metric.type) {
    case 'postgres':
      if (Array.isArray(result) && result.length > 0) {
        const data = result[0];

        // Para métricas com threshold
        if (metric.threshold) {
          const metricValue = data[Object.keys(data)[0]];
          value = metricValue;

          if (typeof metricValue === 'number' && metricValue > metric.threshold) {
            status = 'warning';
            message = `Valor ${metricValue} excede threshold ${metric.threshold}`;
          }
        }

        // Para data_freshness
        if (metric.name === 'data_freshness') {
          const staleDatasets = result.filter(row => row.hours_since_update > 24);
          if (staleDatasets.length > 0) {
            status = 'warning';
            message = `Datasets desatualizados: ${staleDatasets.map(d => d.dataset_id).join(', ')}`;
            details.stale_datasets = staleDatasets;
          }
        }

        // Para error_rate
        if (metric.name === 'error_rate' && data.critical_errors > 0) {
          status = 'warning';
          message = `${data.critical_errors} erros críticos nas últimas 24h`;
          value = data.error_count;
        }

        // Para ingestion_status
        if (metric.name === 'ingestion_status') {
          const staleDatasets = result.filter(row => row.status === 'stale');
          if (staleDatasets.length > 0) {
            status = 'warning';
            message = `${staleDatasets.length} datasets sem atualização há mais de 24h`;
            details.datasets = staleDatasets;
          }
        }

        // Para chat_usage
        if (metric.name === 'chat_usage') {
          value = data.total_messages;
          details = data;
        }
      }
      break;

    case 'redis':
      if (metric.expected) {
        if (result !== metric.expected) {
          status = 'error';
          message = `Esperado ${metric.expected}, recebido ${result}`;
        }
      } else if (metric.threshold) {
        // Para contagem (como sessões ativas)
        value = typeof result === 'number' ? result : (Array.isArray(result) ? result.length : 0);
        if (value > metric.threshold) {
          status = 'warning';
          message = `Contagem ${value} excede threshold ${metric.threshold}`;
        }
      }
      break;

    case 'http':
      const responseTime = result.responseTime || 
        (result.timings ? result.timings.total : 0);
      const statusCode = result.statusCode || result.status;

      value = responseTime;
      details = {
        statusCode: statusCode,
        responseTime: responseTime
      };

      if (statusCode !== 200 && statusCode !== 201) {
        status = 'error';
        message = `HTTP ${statusCode}`;
      } else if (metric.threshold && responseTime > metric.threshold) {
        status = 'warning';
        message = `Resposta lenta: ${responseTime}ms`;
      }
      break;
  }
}

// Determinar severidade
const severity = 
  (metric.critical && status === 'error') ? 'critical' :
  (status === 'error') ? 'high' :
  (status === 'warning') ? 'medium' : 'low';

// Adicionar contexto baseado no histórico (se disponível)
const previousStatus = $('get-previous-status').first()?.json;
if (previousStatus) {
  if (previousStatus.status === 'ok' && status !== 'ok') {
    details.status_change = 'degraded';
    details.previous_status = previousStatus.status;
  } else if (previousStatus.status !== 'ok' && status === 'ok') {
    details.status_change = 'recovered';
    details.previous_status = previousStatus.status;
  }
}

return {json: {
  metric_name: metric.name,
  metric_description: metric.description,
  status: status,
  value: value,
  message: message,
  severity: severity,
  timestamp: checkTime.toISOString(),
  details: {
    type: metric.type,
    threshold: metric.threshold,
    critical: metric.critical,
    raw_result: result,
    ...details
  }
}};

const alerts = $input.all().map(item => item.json);
const timestamp = new Date();

// Agrupar alertas por severidade
const grouped = {
  critical: [],
  high: [],
  medium: [],
  low: []
};

alerts.forEach(alert => {
  if (grouped[alert.severity]) {
    grouped[alert.severity].push(alert);
  }
});

// Construir mensagem de alerta
let message = '🚨 **ALERTA - AIDE - Monitoramento**\n\n';
message += `📅 ${timestamp.toLocaleString('pt-BR')}\n\n`;

if (grouped.critical.length > 0) {
  message += '🔴 **CRÍTICO**\n';
  grouped.critical.forEach(a => {
    message += `• ${a.metric_description || a.metric_name}: ${a.message}\n`;
  });
  message += '\n';
}

if (grouped.high.length > 0) {
  message += '🟠 **ALTO**\n';
  grouped.high.forEach(a => {
    message += `• ${a.metric_description || a.metric_name}: ${a.message}\n`;
  });
  message += '\n';
}

if (grouped.medium.length > 0) {
  message += '🟡 **MÉDIO**\n';
  grouped.medium.forEach(a => {
    message += `• ${a.metric_description || a.metric_name}: ${a.message}\n`;
  });
  message += '\n';
}

// Adicionar informações sobre mudanças de status
const statusChanges = alerts.filter(a => a.details?.status_change);
if (statusChanges.length > 0) {
  message += '📊 **Mudanças de Status**\n';
  statusChanges.forEach(a => {
    const emoji = a.details.status_change === 'recovered' ? '✅' : '⚠️';
    message += `${emoji} ${a.metric_name}: ${a.details.status_change}\n`;
  });
  message += '\n';
}

// Determinar canais de notificação
const channels = [];
if (grouped.critical.length > 0) {
  channels.push('telegram', 'email', 'slack', 'webhook');
} else if (grouped.high.length > 0) {
  channels.push('telegram', 'slack');
} else {
  channels.push('slack');
}

// Adicionar recomendações
const recommendations = [];
alerts.forEach(a => {
  if (a.metric_name === 'data_freshness' && a.status !== 'ok') {
    recommendations.push('Verificar processo de ingestão de dados');
  }
  if (a.metric_name === 'disk_usage' && a.value > 8000) {
    recommendations.push('Considerar limpeza de dados antigos ou expansão de armazenamento');
  }
  if (a.metric_name === 'error_rate' && a.value > 50) {
    recommendations.push('Revisar logs de erro para identificar padrões de falha');
  }
  if (a.metric_name === 'api_response_time' && a.status === 'warning') {
    recommendations.push('Otimizar queries ou aumentar recursos do servidor');
  }
});

if (recommendations.length > 0) {
  message += '💡 **Recomendações**\n';
  recommendations.forEach(r => {
    message += `• ${r}\n`;
  });
}

return {json: {
  message: message,
  channels: channels,
  alert_count: alerts.length,
  critical_count: grouped.critical.length,
  high_count: grouped.high.length,
  medium_count: grouped.medium.length,
  alerts: grouped,
  recommendations: recommendations,
  timestamp: timestamp.toISOString()
}};

// Processar métricas externas recebidas via webhook
const input = $input.first().json;
const timestamp = new Date();

// Validar entrada
if (!input.workflow_name || !input.metrics) {
  throw new Error('workflow_name e metrics são obrigatórios');
}

// Processar métricas por workflow
let processedMetric = {};

switch(input.workflow_name) {
  case 'data_ingestion':
    processedMetric = {
      metric_name: `ingestion_${input.dataset_id || 'unknown'}`,
      metric_description: `Ingestão de dados: ${input.dataset_name || input.dataset_id}`,
      status: input.status || 'unknown',
      value: input.records_processed || 0,
      message: `${input.action || 'processed'} ${input.records_processed || 0} registros em ${input.processing_time_ms || 0}ms`,
      severity: input.status === 'error' ? 'high' : 'low',
      timestamp: timestamp.toISOString(),
      details: {
        workflow: 'data_ingestion',
        batch_id: input.batch_id,
        priority: input.priority,
        ...input.metadata
      }
    };
    break;

  case 'chat_processing':
    const metrics = input.metrics;
    processedMetric = {
      metric_name: 'chat_request',
      metric_description: 'Processamento de chat',
      status: 'ok',
      value: metrics.processing_time_ms || 0,
      message: `Chat processado em ${metrics.processing_time_ms}ms, ${metrics.tokens_used || 0} tokens`,
      severity: 'low',
      timestamp: timestamp.toISOString(),
      details: {
        workflow: 'chat_processing',
        model: metrics.model,
        tokens: metrics.tokens_used,
        data_freshness: metrics.data_freshness,
        function_called: metrics.function_called
      }
    };
    break;

  default:
    processedMetric = {
      metric_name: input.metric_name || 'external_metric',
      metric_description: input.description || 'Métrica externa',
      status: input.status || 'unknown',
      value: input.value || 0,
      message: input.message || 'Métrica recebida',
      severity: input.severity || 'low',
      timestamp: timestamp.toISOString(),
      details: input
    };
}

return {json: processedMetric};

// Gerar relatório consolidado de saúde do sistema
const allMetrics = $('save-metric').all().map(item => item.json);
const timestamp = new Date();

// Calcular estatísticas
const stats = {
  total_checks: allMetrics.length,
  healthy: allMetrics.filter(m => m.status === 'ok').length,
  warnings: allMetrics.filter(m => m.status === 'warning').length,
  errors: allMetrics.filter(m => m.status === 'error').length,
  critical: allMetrics.filter(m => m.severity === 'critical').length
};

// Calcular health score (0-100)
let healthScore = 100;
stats.errors > 0 && (healthScore -= stats.errors * 10);
stats.warnings > 0 && (healthScore -= stats.warnings * 5);
stats.critical > 0 && (healthScore -= stats.critical * 20);
healthScore = Math.max(0, healthScore);

// Determinar status geral
let overallStatus = 'healthy';
if (healthScore < 50 || stats.critical > 0) {
  overallStatus = 'critical';
} else if (healthScore < 75 || stats.errors > 0) {
  overallStatus = 'degraded';
} else if (healthScore < 90 || stats.warnings > 0) {
  overallStatus = 'warning';
}

// Agrupar métricas por categoria
const categorizedMetrics = {
  infrastructure: [],
  data: [],
  application: [],
  usage: []
};

allMetrics.forEach(m => {
  const category = 
    ['database_health', 'redis_health', 'disk_usage'].includes(m.metric_name) ? 'infrastructure' :
    ['data_freshness', 'ingestion_status'].includes(m.metric_name) ? 'data' :
    ['api_response_time', 'error_rate', 'active_sessions'].includes(m.metric_name) ? 'application' :
    'usage';

  categorizedMetrics[category].push({
    name: m.metric_name,
    status: m.status,
    value: m.value,
    message: m.message
  });
});

// Gerar recomendações baseadas no estado atual
const recommendations = [];

if (stats.critical > 0) {
  recommendations.push({
    priority: 'high',
    action: 'Investigar imediatamente os alertas críticos'
  });
}

allMetrics.forEach(m => {
  if (m.metric_name === 'data_freshness' && m.status !== 'ok') {
    recommendations.push({
      priority: 'medium',
      action: 'Verificar processo de ingestão de dados',
      details: m.details?.stale_datasets
    });
  }

  if (m.metric_name === 'disk_usage' && m.value > 8000) {
    recommendations.push({
      priority: 'medium',
      action: 'Planejar limpeza de dados ou expansão de armazenamento',
      details: `Uso atual: ${m.value}MB`
    });
  }

  if (m.metric_name === 'error_rate' && m.value > 50) {
    recommendations.push({
      priority: 'high',
      action: 'Analisar logs de erro para identificar causa raiz',
      details: m.details
    });
  }
});

// Criar relatório completo
const report = {
  timestamp: timestamp.toISOString(),
  health_score: healthScore,
  status: overallStatus,
  statistics: stats,
  categories: categorizedMetrics,
  metrics_summary: allMetrics.map(m => ({
    name: m.metric_name,
    status: m.status,
    value: m.value,
    severity: m.severity,
    message: m.message
  })),
  recommendations: recommendations,
  metadata: {
    check_interval: '5 minutes',
    last_check: timestamp.toISOString(),
    version: '1.0.0'
  }
};

return {json: report};
