    default:
      // CORRE√á√ÉO: Para "√∫ltimos 3 meses", vamos buscar dados de 90 dias atr√°s
      startDate.setDate(startDate.getDate() - 90);
  }
} else {
  // CORRE√á√ÉO: Padr√£o ampliado - √∫ltimos 30 dias em vez de 7
  startDate.setDate(startDate.getDate() - 30);
}

// Formatear datas para PostgreSQL
const startDateStr = `'${startDate.toISOString()}'`;
