  query = `
    SELECT 
      dataset_id,
      period_start,
      subsystem,
      metric_name,
      value,
      unit,
      created_at
    FROM data_records
    WHERE period_start >= ${startDateStr}
    ORDER BY period_start DESC
    LIMIT 50;
  `;
}

return {json: {
