  dataset_id: template?.dataset_id || intent,
  check_freshness: analysis.needs_fresh_data || false
}};

const userMessage = $('Validar e Preparar').first().json.message;
const analysisData = $('Analisar Inten√ß√£o').first().json;
const analysis = analysisData.analysis;
const queryResults = $('Executar Query').all().map(item => item.json);

let dataContext = '';
let foundData = false;

if (queryResults && queryResults.length > 0) {
  const data = queryResults.flat();
  foundData = data.length > 0;

  if (foundData) {
    dataContext = `DADOS DISPON√çVEIS NO SISTEMA:\n\n`;

    // Dados de carga de energia
    const cargaData = data.filter(row => row.dataset_id === 'carga_energia');
    if (cargaData.length > 0) {
      dataContext += `CARGA DE ENERGIA (dados de ${cargaData[0].period_start}):\n`;
      cargaData.forEach(row => {
        dataContext += `‚Ä¢ ${row.subsystem}: ${row.value} ${row.unit}\n`;
      });
      dataContext += `\n`;
    }

    // Outros dados dispon√≠veis
    const outrosDados = data.filter(row => row.dataset_id !== 'carga_energia');
    if (outrosDados.length > 0) {
      outrosDados.forEach(row => {
        dataContext += `‚Ä¢ ${row.dataset_id.toUpperCase()}: ${row.subsystem} = ${row.value} ${row.unit}\n`;
      });
    }
  }
}

const fullContext = `${userMessage}

INFORMA√á√ïES DISPON√çVEIS:
${foundData ? dataContext : 'Dados n√£o est√£o carregados no momento.'}

INSTRU√á√ïES IMPORTANTES:
- Voc√™ √© o AIDE, assistente de dados energ√©ticos do ONS
- Use APENAS os dados fornecidos acima
- N√ÉO mencione "documentos" ou "arquivos n√£o acess√≠veis"
- ${foundData ? 'Analise os dados espec√≠ficos e forne√ßa insights baseados neles' : 'Informe que os dados solicitados n√£o est√£o dispon√≠veis no momento'}
- Sempre responda em portugu√™s brasileiro`;

const contextData = $('Validar e Preparar').first().json;

return {json: {
  chatInput: fullContext,
  text: fullContext,
  message: fullContext,
  metadata: {
    session_id: contextData.session_id,
