const aiResponse = $json;
const metadata = $('Preparar Contexto IA').first().json.metadata;
const queryData = $('Executar Query').all().map(item => item.json);
const startTime = new Date($('Validar e Preparar').first().json.timestamp);

// Extrair resposta do Assistant
let responseContent = '';
let functionCall = null;

// O Assistant retorna formato diferente do Chat
if (aiResponse.text) {
  responseContent = aiResponse.text;
} else if (aiResponse.choices && aiResponse.choices[0]) {
  const choice = aiResponse.choices[0];
  responseContent = choice.message.content || '';

  if (choice.message.function_call) {
    try {
      functionCall = {
        name: choice.message.function_call.name,
        arguments: JSON.parse(choice.message.function_call.arguments)
      };
    } catch (e) {
      console.error('Error parsing function call:', e);
    }
  }
} else {
  responseContent = String(aiResponse);
}

// Determinar necessidade de visualiza√ß√£o
const needsVisualization = functionCall?.name === 'generate_visualization' ||
  responseContent.toLowerCase().includes('gr√°fico') ||
  responseContent.toLowerCase().includes('visualiza√ß√£o') ||
  metadata.intent === 'comparacao' ||
  (queryData && queryData.length > 5);

// Preparar dados para visualiza√ß√£o
function prepareVisualizationData(intent, data) {
  if (!data || data.length === 0) return null;

  const flatData = data.flat();

  if (intent === 'carga_energia') {
    const timeData = flatData
      .filter(row => row.period && !row.subsystem?.includes('_stats'))
      .sort((a, b) => new Date(a.period) - new Date(b.period));

    if (timeData.length === 0) return null;

    const subsystems = [...new Set(timeData.map(row => row.subsystem))];

    return {
      type: 'line',
      title: 'Evolu√ß√£o da Carga de Energia',
      data: {
        labels: [...new Set(timeData.map(row => new Date(row.period).toISOString()))],
        datasets: subsystems.map(subsystem => ({
          label: subsystem,
          data: timeData
            .filter(row => row.subsystem === subsystem)
            .map(row => row.avg_load),
          borderColor: getColor(subsystem),
          fill: false
        }))
      },
      options: {
        responsive: true,
        scales: {
          y: {
            title: {
              display: true,
              text: 'Carga (MWmed)'
            }
          }
        }
      }
    };
  } else if (intent === 'cmo_pld') {
    const priceData = flatData
      .filter(row => row.period)
      .sort((a, b) => new Date(a.period) - new Date(b.period));

    return {
      type: 'bar',
      title: 'CMO/PLD por Subsistema',
      data: {
        labels: [...new Set(priceData.map(row => row.subsystem))],
        datasets: [{
          label: 'Pre√ßo M√©dio (R$/MWh)',
          data: priceData.map(row => row.avg_price),
          backgroundColor: 'rgba(54, 162, 235, 0.5)'
        }]
      }
    };
  } else if (intent === 'bandeiras') {
    const flagData = flatData.slice(0, 12);

    return {
      type: 'line',
      title: 'Hist√≥rico de Bandeiras Tarif√°rias',
      data: {
        labels: flagData.map(row => new Date(row.period).toLocaleDateString('pt-BR')),
        datasets: [{
          label: 'Valor (R$/100kWh)',
          data: flagData.map(row => row.tariff_value),
          borderColor: 'rgb(255, 99, 132)',
          stepped: true
        }]
      }
    };
  }

  return null;
}

// Fun√ß√£o auxiliar para cores
function getColor(subsystem) {
  const colors = {
    'Sudeste': '#1f77b4',
    'Sul': '#ff7f0e',
    'Nordeste': '#2ca02c',
    'Norte': '#d62728',
    'Centro-Oeste': '#9467bd'
  };
  return colors[subsystem] || '#666666';
}

// Gerar sugest√µes contextuais
function generateSuggestions(intent, entities) {
  const suggestions = {
    'carga_energia': [
      'Comparar com per√≠odo anterior',
      'Ver previs√£o para pr√≥ximas horas',
      'Analisar pico de consumo',
      entities && entities.regions && entities.regions.length === 0 ? 'Comparar entre regi√µes' : 'Detalhar dados regionais'
    ],
    'cmo_pld': [
      'Comparar patamares de carga',
      'Ver hist√≥rico semanal',
      'Analisar tend√™ncia de pre√ßos',
      'Comparar com mesmo per√≠odo ano anterior'
    ],
    'bandeiras': [
      'Ver impacto na conta de luz',
      'Hist√≥rico do √∫ltimo ano',
      'Previs√£o para pr√≥ximo m√™s',
      'Comparar com infla√ß√£o'
    ],
    'default': [
      'Ver dados mais recentes',
      'Comparar per√≠odos',
      'Exportar relat√≥rio',
      'Fazer an√°lise detalhada'
    ]
  };

  return suggestions[intent] || suggestions.default;
}

// Calcular m√©tricas de processamento
const processingTime = Date.now() - new Date(startTime).getTime();

// Obter dados da an√°lise
const analysisData = $('Analisar Inten√ß√£o').first().json.analysis;

// Estruturar resposta (vers√£o melhorada e √∫nica)
const structuredResponse = {
  session_id: metadata.session_id,
  user_id: metadata.user_id,
  request_id: metadata.request_id,
  response: {
    text: responseContent,
    intent: metadata.intent,
    confidence: analysisData.confidence,
    data_summary: metadata.statistics || {},
    raw_data: queryData && queryData.length > 0 ? queryData.flat().slice(0, 100) : [],
    data_found: queryData && queryData.length > 0 && queryData.flat().length > 0,
    visualization: needsVisualization ? 
      (functionCall?.arguments || prepareVisualizationData(metadata.intent, queryData)) : 
      null,
    suggestions: generateSuggestions(
      metadata.intent,
      analysisData.entities
    ),
    metadata: {
      processing_time_ms: processingTime,
      tokens_used: aiResponse.usage?.total_tokens || aiResponse.tokens_used || 0,
      model: aiResponse.model || 'assistant',
      data_freshness: metadata.data_age || { status: 'unknown' },
      function_called: functionCall?.name || null,
      type: (queryData && queryData.length > 0 && queryData.flat().length > 0) ? 'data_response' : 'simple_response',
      query_executed: queryData && queryData.length > 0,
      total_records: queryData ? queryData.flat().length : 0
    }
  },
  success: true
};

return {json: structuredResponse};

