const onsDatasets = items[0].json;  // Pode n√£o ter .datasets// Comparar datasets ONS com BD e identificar atualiza√ß√µes
const onsResponse = items[0].json;
const dbDatasets = items[1].json;

// Ajustar para estrutura da API ONS
const onsDatasets = onsResponse.datasets || onsResponse || [];
const datasetsToUpdate = [];
const criticalDatasets = ['carga_energia', 'cmo_pld', 'bandeiras_tarifarias'];

// Verificar se recebemos dados v√°lidos
if (!Array.isArray(onsDatasets)) {
  console.log('‚ö†Ô∏è Dados ONS inv√°lidos:', onsResponse);
  return [];
}

if (!Array.isArray(dbDatasets)) {
  console.log('‚ö†Ô∏è Dados BD inv√°lidos:', dbDatasets);
  return [];
}

for (const onsDataset of onsDatasets) {
  const dbDataset = dbDatasets.find(d => d.external_id === onsDataset.id);

  if (!dbDataset) {
    datasetsToUpdate.push({
      ...onsDataset,
      action: 'create',
      priority: criticalDatasets.includes(onsDataset.id) ? 'critical' : 'high',
      version: onsDataset.version || '1.0'
    });
  } else {
    const lastUpdate = new Date(dbDataset.last_updated);
    const onsUpdate = new Date(onsDataset.updated_at);

    if (onsUpdate > lastUpdate || onsDataset.version !== dbDataset.version) {
      datasetsToUpdate.push({
        ...onsDataset,
        action: 'update',
        priority: criticalDatasets.includes(onsDataset.id) ? 'critical' : 'normal',
        version: onsDataset.version || dbDataset.version
      });
    }
  }
}

// Ordenar por prioridade
datasetsToUpdate.sort((a, b) => {
  const priorityOrder = { critical: 0, high: 1, normal: 2 };
  return priorityOrder[a.priority] - priorityOrder[b.priority];
});

// Log para debugging
console.log(`üìä Encontrados ${datasetsToUpdate.length} datasets para atualizar`);
console.log('üîÑ Datasets:', datasetsToUpdate.map(d => `${d.id} (${d.action}, ${d.priority})`));

// Adicionar timestamp de processamento
return datasetsToUpdate.map(dataset => ({
  json: {
    ...dataset,
    processing_start: new Date().toISOString(),
    batch_id: `batch_${Date.now()}`
  }
}));

const rawData = $input.first().json;
const datasetInfo = $('split-batches').first().json;
const datasetId = datasetInfo.id;

// Valida√ß√µes aprimoradas
if (!rawData) {
  throw new Error(`Resposta vazia da API ONS para dataset ${datasetId}`);
}

if (!rawData.data || !Array.isArray(rawData.data)) {
  console.log(`‚ö†Ô∏è Estrutura de dados inesperada para ${datasetId}:`, rawData);
  throw new Error(`Dados inv√°lidos para dataset ${datasetId} - esperado array em 'data'`);
}

if (rawData.data.length === 0) {
  console.log(`‚ÑπÔ∏è Nenhum dado novo encontrado para ${datasetId}`);
  return []; // Retorna array vazio se n√£o h√° dados
}

console.log(`üìä Processando ${rawData.data.length} registros do dataset ${datasetId}`);

let transformedData = [];
const processingTime = new Date();

// Fun√ß√£o auxiliar para validar e parsear datas
function parseDate(dateStr, fallback = processingTime) {
  if (!dateStr) return fallback;
  const parsed = new Date(dateStr);
  return isNaN(parsed.getTime()) ? fallback : parsed;
}

// Fun√ß√£o auxiliar para validar e parsear n√∫meros
function parseValue(value, fallback = 0) {
  const parsed = parseFloat(value);
  return isNaN(parsed) ? fallback : parsed;
}

switch(datasetId) {
  case 'carga_energia':
    transformedData = rawData.data.map(row => {
      const timestamp = parseDate(row.din_instante);

      return {
        dataset_id: datasetId,
        period_start: timestamp,
        period_end: timestamp,
        year: timestamp.getFullYear(),
        month: timestamp.getMonth() + 1,
        day: timestamp.getDate(),
        hour: timestamp.getHours(),
        region: row.nom_subsistema || 'Unknown',
        subsystem: row.nom_subsistema || 'Unknown',
        metric_type: 'load',
        metric_name: 'carga_energia',
        value: parseValue(row.val_cargaenergiamwmed),
        unit: 'MWmed',
        raw_data: row,
        processed_data: {
          id_subsistema: row.id_subsistema,
          quality_flag: row.quality || 'verified',
          processing_version: datasetInfo.version,
          data_source: 'ONS_API'
        }
      };
    });
    break;

  case 'cmo_pld':
    transformedData = rawData.data.map(row => {
      const timestamp = parseDate(row.din_instante);

      return {
        dataset_id: datasetId,
        period_start: timestamp,
        period_end: timestamp,
        year: timestamp.getFullYear(),
        month: timestamp.getMonth() + 1,
        day: timestamp.getDate(),
        hour: 0,
        region: row.nom_subsistema || 'Unknown',
        subsystem: row.nom_subsistema || 'Unknown',
        metric_type: 'price',
        metric_name: 'cmo',
        value: parseValue(row.val_cmomediasemanal || row.val_cmo),
        unit: 'R$/MWh',
        raw_data: row,
        processed_data: {
          patamar: row.patamar || 'medio',
          tipo: row.tipo_preco || 'cmo',
          processing_version: datasetInfo.version,
          data_source: 'ONS_API'
        }
      };
    });
    break;

  case 'bandeiras_tarifarias':
    transformedData = rawData.data.map(row => {
      const timestamp = parseDate(row.DatCompetencia);

      return {
        dataset_id: datasetId,
        period_start: timestamp,
        period_end: timestamp,
        year: timestamp.getFullYear(),
        month: timestamp.getMonth() + 1,
        day: 1,
        hour: 0,
        region: 'Brasil',
        subsystem: 'Nacional',
        metric_type: 'tariff',
        metric_name: 'bandeira_tarifaria',
        value: parseValue(row.VlrAdicionalBandeira),
        unit: 'R$/100kWh',
        raw_data: row,
        processed_data: {
          bandeira: row.NomBandeiraAcionada || 'Unknown',
          vigencia: row.DatGeracaoConjuntoDados,
          processing_version: datasetInfo.version,
          data_source: 'ONS_API'
        }
      };
    });
    break;

  default:
    console.log(`‚ö†Ô∏è Dataset n√£o reconhecido: ${datasetId}. Usando transforma√ß√£o gen√©rica.`);
    transformedData = rawData.data.map(row => ({
      dataset_id: datasetId,
      period_start: processingTime,
      period_end: processingTime,
      year: processingTime.getFullYear(),
      month: processingTime.getMonth() + 1,
      day: processingTime.getDate(),
      hour: processingTime.getHours(),
      region: 'Unknown',
      subsystem: 'Unknown',
      metric_type: 'generic',
      metric_name: datasetId,
      value: 0,
      unit: 'unit',
      raw_data: row,
      processed_data: {
        processing_version: datasetInfo.version,
        data_source: 'ONS_API',
        note: 'Generic transformation applied'
      }
    }));
}

// Filtrar registros inv√°lidos (com datas muito antigas ou muito futuras)
const validData = transformedData.filter(item => {
  const year = item.year;
  return year >= 2020 && year <= 2030; // Filtro de sanidade
});

if (validData.length !== transformedData.length) {
  console.log(`‚ö†Ô∏è Filtrados ${transformedData.length - validData.length} registros com datas inv√°lidas`);
}

console.log(`‚úÖ Transforma√ß√£o conclu√≠da: ${validData.length} registros v√°lidos`);

// Adicionar metadados de processamento
return validData.map(item => ({
  json: {
    ...item,
    created_at: processingTime.toISOString(),
    batch_id: datasetInfo.batch_id,
    processing_timestamp: Date.now()
  }
}));

// Coletar m√©tricas de ingest√£o
const dataset = $('split-batches').first().json;
const insertResult = $('insert-data').first()?.json;
const updateResult = $('update-dataset-status').first()?.json;
const transformData = $('transform-data').all();

// Valida√ß√µes b√°sicas
if (!dataset) {
  console.log('‚ùå Erro: Informa√ß√µes do dataset n√£o encontradas');
  return {json: {
    workflow_name: 'data_ingestion',
    status: 'error',
    error: 'Dataset information missing',
    timestamp: new Date().toISOString()
  }};
}

const processingEnd = new Date();
const processingStart = new Date(dataset.processing_start);
const processingTime = processingEnd - processingStart;

// Calcular estat√≠sticas detalhadas
const recordsProcessed = transformData.length;
const recordsPerSecond = processingTime > 0 ? Math.round((recordsProcessed * 1000) / processingTime) : 0;

// Detectar poss√≠veis problemas
const hasInsertErrors = !insertResult || insertResult.error;
const hasUpdateErrors = !updateResult || updateResult.error;
const processingStatus = hasInsertErrors || hasUpdateErrors ? 'partial_success' : 'success';

// M√©tricas de performance
const performanceMetrics = {
  processing_time_ms: processingTime,
  processing_time_seconds: Math.round(processingTime / 1000),
  records_per_second: recordsPerSecond,
  avg_time_per_record_ms: recordsProcessed > 0 ? Math.round(processingTime / recordsProcessed) : 0
};

// M√©tricas de qualidade dos dados
const dataQualityMetrics = {
  total_records_input: recordsProcessed,
  records_inserted: insertResult?.affectedRows || insertResult?.rowCount || recordsProcessed,
  success_rate: recordsProcessed > 0 ? Math.round((insertResult?.affectedRows || recordsProcessed) / recordsProcessed * 100) : 100
};

const metrics = {
  workflow_name: 'data_ingestion',
  dataset_id: dataset.id,
  dataset_name: dataset.name || dataset.id,
  batch_id: dataset.batch_id,
  priority: dataset.priority,
  action: dataset.action,
  records_processed: recordsProcessed,
  processing_time_ms: processingTime,
  status: processingStatus,
  timestamp: processingEnd.toISOString(),

  // M√©tricas detalhadas
  performance: performanceMetrics,
  data_quality: dataQualityMetrics,

  // Metadados expandidos
  metadata: {
    version: dataset.version,
    processing_start: dataset.processing_start,
    processing_end: processingEnd.toISOString(),

    // Dados do update
    row_count_after: updateResult?.row_count,
    last_updated: updateResult?.last_updated,

    // Informa√ß√µes de ingest√£o
    ingestion_info: updateResult?.last_ingestion_info,

    // Status dos n√≥s anteriores
    insert_status: hasInsertErrors ? 'error' : 'success',
    update_status: hasUpdateErrors ? 'error' : 'success',

    // Informa√ß√µes do sistema
    n8n_execution_id: $execution.id,
    workflow_version: '1.0.0'
  },

  // Alertas se houver problemas
  alerts: [
    ...(processingTime > 300000 ? ['Processing time exceeded 5 minutes'] : []),
    ...(recordsPerSecond < 10 && recordsProcessed > 100 ? ['Low processing speed detected'] : []),
    ...(hasInsertErrors ? ['Insert operation had errors'] : []),
    ...(hasUpdateErrors ? ['Update operation had errors'] : [])
  ]
};

// Log das m√©tricas para debug
console.log(`üìä M√©tricas coletadas para ${dataset.id}:`);
console.log(`   Records: ${recordsProcessed}`);
console.log(`   Time: ${Math.round(processingTime/1000)}s`);
console.log(`   Speed: ${recordsPerSecond} records/sec`);
console.log(`   Status: ${processingStatus}`);

if (metrics.alerts.length > 0) {
  console.log(`‚ö†Ô∏è Alertas: ${metrics.alerts.join(', ')}`);
}

return {json: metrics};

// Tratar erros do workflow de ingest√£o
const error = $execution.error || {};
const dataset = $('split-batches').first()?.json || {};
const currentNode = $node.name || 'unknown';
const executionId = $execution.id || 'unknown';

// Tentar capturar informa√ß√µes do n√≥ que falhou
const failedNodeData = $input.first()?.json || {};
const httpError = failedNodeData.statusCode || failedNodeData.status;

// Classificar tipo de erro
let errorType = 'unknown';
let severity = 'medium';

if (error.message?.includes('timeout') || error.message?.includes('TIMEOUT')) {
  errorType = 'timeout';
  severity = 'high';
} else if (error.message?.includes('ECONNREFUSED') || error.message?.includes('ENOTFOUND')) {
  errorType = 'connection';
  severity = 'critical';
} else if (httpError >= 400 && httpError < 500) {
  errorType = 'client_error';
  severity = httpError === 404 ? 'medium' : 'high';
} else if (httpError >= 500) {
  errorType = 'server_error';
  severity = 'critical';
} else if (error.message?.includes('SQL') || error.message?.includes('database')) {
  errorType = 'database';
  severity = 'critical';
} else if (error.message?.includes('JSON') || error.message?.includes('parse')) {
  errorType = 'data_format';
  severity = 'medium';
}

// Determinar se √© erro cr√≠tico (para datasets priorit√°rios)
const isCriticalDataset = ['carga_energia', 'cmo_pld', 'bandeiras_tarifarias'].includes(dataset.id);
if (isCriticalDataset && severity !== 'medium') {
  severity = 'critical';
}

const errorLog = {
  workflow: 'data_ingestion',
  execution_id: executionId,
  node_name: currentNode,
  dataset_id: dataset.id || 'unknown',
  dataset_name: dataset.name || dataset.id || 'unknown',
  batch_id: dataset.batch_id || 'unknown',
  timestamp: new Date().toISOString(),

  // Informa√ß√µes do erro
  error: {
    type: errorType,
    severity: severity,
    message: error.message || 'Unknown error',
    stack: error.stack,
    code: error.code,
    node: error.node || currentNode,
    http_status: httpError,
    details: error.details || failedNodeData
  },

  // Contexto do dataset
  context: {
    priority: dataset.priority || 'unknown',
    action: dataset.action || 'unknown',
    version: dataset.version,
    processing_start: dataset.processing_start,
    is_critical_dataset: isCriticalDataset
  },

  // Informa√ß√µes para recupera√ß√£o
  recovery: {
    retry_recommended: errorType === 'timeout' || errorType === 'connection',
    manual_check_needed: errorType === 'data_format' || severity === 'critical',
    skip_dataset: errorType === 'client_error' && httpError === 404
  },

  // Metadados t√©cnicos
  technical_info: {
    n8n_version: $vars.N8N_VERSION || 'unknown',
    workflow_version: '1.0.0',
    environment: 'development',
    user_agent: 'N8N-ASPI-DataIngestion'
  }
};

// Log estruturado para debug
console.log(`‚ùå ERRO no workflow de ingest√£o:`);
console.log(`   Dataset: ${errorLog.dataset_id}`);
console.log(`   Tipo: ${errorLog.error.type}`);
console.log(`   Severidade: ${errorLog.error.severity}`);
console.log(`   N√≥: ${errorLog.node_name}`);
console.log(`   Mensagem: ${errorLog.error.message}`);

if (errorLog.recovery.retry_recommended) {
  console.log(`üîÑ Retry recomendado para este tipo de erro`);
}

if (errorLog.recovery.manual_check_needed) {
  console.log(`‚ö†Ô∏è Verifica√ß√£o manual necess√°ria - erro cr√≠tico`);
}

return {json: errorLog};

